Stores.Store:
{
  xtype : "Store",
  classname : "ProcessMonitorStore",
  type : "JrafXmlStore",
  recordType : ProcessMonitorRec,
  idProperty : "processName",
  api : {
    read :     {
      sysName : "pcmc",
      oprID : "ProcessMonitor",
      actions : "queryProcessInstance"
  },
    create :     {
      sysName : "pcmc",
      oprID : "ProcessMonitor",
      actions : "queryProcessInstance"
  },
    update :     {
      sysName : "pcmc",
      oprID : "ProcessMonitor",
      actions : "queryProcessInstance"
  },
    destroy :     {
      sysName : "pcmc",
      oprID : "ProcessMonitor",
      actions : "queryProcessInstance"
  }
},
  autoLoad : false,
  autoSave : false,
  paramsAsHash : true,
  remoteSort : true,
  listeners : {
    write : function (store,action,result,res,rs){if (res.success){if (_editInfoWin){_editInfoWin.hide();}}}
}
}

SubPanels.Panel:
{
  xtype : "panel",
  classname : "EditInfoForm",
  frame : true,
  bodyBorder : false,
  items : [    {
      layout : "column",
      bodyBorder : false,
      items : [        {
          layout : "form",
          bodyBorder : false,
          columnWidth : 0.5,
          labelAlign : "right",
          items : [            {
              name : "processName",
              fieldLabel : "流程名称",
              width : 150,
              allowBlank : true,
              xtype : "textfield"
          }]
      }]
  }],
  id : "EditInfoForm",
  buttonAlign : "center",
  buttons : [    {
      text : "保存",
      width : 100,
      height : 25,
      iconCls : "disk",
      handler : function() {var _forma = Ext.getCmp('EditInfoForm');if (_forma.getForm().isValid()){if(_forma.record){_forma.getForm().updateRecord(_forma.record);ProcessMonitorStore.save();}}}
  },    {
      text : "取消",
      width : 100,
      height : 25,
      iconCls : "arrow-undo",
      handler : function() {_editInfoWin.hide();}
  }]
}

view.MainPanel:
[  {
    xtype : "panel",
    layout : "border",
    items : [      {
        xtype : "form",
        region : "north",
        autoHeight : true,
        title : "查询条件",
        frame : true,
        items : [          {
            name : "processKey",
            fieldLabel : "流程key",
            width : 150,
            allowBlank : true,
            xtype : "textfield"
        }],
        buttonAlign : "center",
        id : "ProcessMonitorForm",
        buttons : [        {
          text : "查询",
          handler : function(){var qStore=Ext.getCmp('ProcessMonitorGrid').getStore();qStore.setFormParam(Ext.getCmp('ProcessMonitorForm'));qStore.setPageInfo(JrafSession.get('PageSize'),'1');qStore.load();}
      }]
    },      {
        xtype : "grid",
        id : "ProcessMonitorGrid",
        region : "center",
        autoWidth : true,
        frame : true,
        title : "查询结果",
        viewConfig : {
        forceFit : false
    },
        stripeRows : true,
        columnLines : true,
        autoHeight : false,
        height : 320,
        store : ProcessMonitorStore,
        bbar : new Ext.PagingToolbar({pageSize: JrafSession.get('PageSize'),store:ProcessMonitorStore,displayInfo: true}),
        tbar : [{text:'新增',iconCls:'add',ref: '../addBtn',handler:function(){var _grid=this.ownerCt.ownerCt;var recordType=_grid.getStore().recordType;var nr=new recordType();_grid.getStore().add(nr);_grid.getSelectionModel().selectLastRow();openEditInfoWin(nr);}},{xtype: 'tbseparator'},{text:'删除',iconCls:'delete',ref: '../removeBtn',disabled: true,handler:function(){var _grid=this.ownerCt.ownerCt;var ckrs=_grid.getSelectionModel().getSelections();for(var i=0;i<ckrs.length;i++){_grid.getStore().remove(ckrs[i]);}}},{xtype: 'tbseparator'},{text:'保存',iconCls:'disk',ref: '../saveBtn',handler:function(){var _grid=this.ownerCt.ownerCt;Ext.Msg.confirm('保存数据','确认保存已修改数据?',function(btn){if(btn == 'yes'){_grid.getStore().save();}});}}],
        colModel : new Ext.grid.ColumnModel([new Ext.grid.CheckboxSelectionModel(),{width:100,sortable:true,header:'流程名称',dataIndex:'processName',renderer:Ext.util.Format.paramRenderer('undefined','')}]),
        sm : new Ext.grid.CheckboxSelectionModel({ listeners:{selectionchange:function(sm){var _grid=this.grid;if(sm.getCount()){_grid.removeBtn.enable();}else{_grid.removeBtn.disable();}}}}),
        listeners : {rowdblclick : function(g,rowIndex,e){var _rec=g.getSelectionModel().getSelected();openEditInfoWin(_rec);}}
    }]
}]
