  function loadRec2(node,	checked) {
	var	sourceIdArray =	node.getPath().split("/");

	if (checked) {
		node.expand();
		var	parentNode = node.parentNode;
		if (parentNode != undefined) {
			parentNode.attributes.checked =	checked;
			var	cb = parentNode.ui.checkbox;
			if (cb)	cb.checked = checked;

			var	intree = Ext.getCmp('outBoundServerType_tree2');
			var	_form =	Ext.getCmp('outBoundsForm');
			var	rs = intree.getChecked();


			var	obj	= _form.getForm().findField("c_summary");
			var	_value = obj.getValue();

			var	valtext	= "";
			valtext	= node.attributes.text;

			if (_value.indexOf(valtext)	< 0) {
				if (_value == '') _value = valtext;
				else _value	= _value + ',' + valtext;

			}
			obj.setValue(_value);

			loadRec2(parentNode, checked);

		}
	} else {
		var	flag = false;
		var	sournode = Ext.getCmp("outBoundServerType_tree2").getNodeById(sourceIdArray[sourceIdArray.length	- 2]);
		sournode.eachChild(function	(child)	{
			if (child.attributes.checked) {
				flag = true;
				return;
			}
		});
		if (!flag) {
			sournode.ui.toggleCheck(false);
			sournode.attributes.checked	= false;
		}
	}
}  



    function loadRec1(node,	checked)
{
 var sourceIdArray =	node.getPath().split("/");
 if (checked)
  {
		node.expand();
		 var parentNode = node.parentNode;
		 if (parentNode != undefined) {
			parentNode.attributes.checked =	checked;
			var	cb = parentNode.ui.checkbox;
			if (cb)	cb.checked = checked;

			var	intree = Ext.getCmp('outBoundServerType_tree1');
			var	_form =	Ext.getCmp('inBoundsForm');
			var	rs = intree.getChecked();


			var	obj	= _form.getForm().findField("c_summary");
			var	_value = obj.getValue();

			var	valtext	= "";
			valtext	= node.attributes.text;

			if (_value.indexOf(valtext)	< 0) {
				if (_value == '') _value = valtext;
				else _value	= _value + ',' + valtext;

			}
			obj.setValue(_value);

			loadRec1(parentNode, checked);

		}
	} else {
		var	flag = false;
		var	sournode = Ext.getCmp("outBoundServerType_tree1").getNodeById(sourceIdArray[sourceIdArray.length	- 2]);
		sournode.eachChild(function	(child)	{
			if (child.attributes.checked) {
				flag = true;
				return;
			}
		});
		if (!flag) {
			sournode.ui.toggleCheck(false);
			sournode.attributes.checked	= false;
		}
	}
}


