	 function renderVsSummary(o, cs, cm){
   var sum = 0; 
	    repleShareStore.each(function(record){
	       sum+=Number(record.data.vs);  
	    }); 
	      totalSum = Math.round(sum*100)/100;
	    return'市值合计：'+ Ext.util.Format.number(totalSum,'0,0.00');
	 }

