  }

function renderCashSummary(value, meta, rec, rowIdx, colIdx, ds){
   var cashValue = value ? Ext.util.Format.number(value,'0,0.00') : '0.00';
	  if(rowIdx==ds.getCount()-1){
			  if(colIdx ==0 || colIdx==1 ){
			     return '合计：' + cashValue;
			  }  
			  else{
			    return value;
			   }
		  }	
		  else{
		      return value;
		  }	
}

