 function renderBalanceSummary(o, cs, cm){
    var sum = 0; 
    totalTranCofimStore.each(function(record){
     var busflag = record.data.c_assbusinflag;
	    if(busflag == '122' || busflag == '134'|| busflag == '130' || busflag == '137' || busflag == '139' || busflag == '144'){
	       sum+=Number(record.data.totalbalance); 
	     }


    }); 
      totalSum = Math.round(sum*100)/100;
    return'增加：'+ Ext.util.Format.number(totalSum,'0,0.00');
}

function renderTSharesSummary(o, cs, cm){
    var sum = 0; 
    totalTranCofimStore.each(function(record){
      var busflag = record.data.c_assbusinflag;
      if(busflag == '124' || busflag == '135'|| busflag == '138' || busflag == '145'){
         sum+=Number(record.data.totalbalance);  
     }
    }); 
     totalSum = Math.round(sum*100)/100;
    return'减少：'+ Ext.util.Format.number(totalSum,'0,0.00');
}


function renderNetValueSummary(o, cs, cm){
