		var inBoundWin;
function openInBoundWin(rec){
	var _formInBound= getInBoundsForm();
			inBoundWin=Ext.getCmp('inBoundWin');
			if(!inBoundWin){
			inBoundWin=new Ext.Window({
			title:'呼入记录',
			id:'inBoundWin',
			layout:'fit',
			width:600,
			height:580,
			closeAction:'close',
			plain: true,
			modal: true,
			resizable :false,
			autoDestroy:true,
			items: _formInBound
			});
	}
		inBoundWin.show();
		_formInBound.getForm().reset();
		_formInBound.getForm().loadRecord(rec);
		_formInBound.record=rec;
};

