		function splitName(value){
		  var opContent = value.split("[/t]");
		  var opId  = "";
		  var opName  = "";
      var content = "";
      var regu = "#";
      var   re   =   new   RegExp(regu); 
      for(var i = 0; i<opContent.length; i++){
	     if(opContent[i].search(re)   !=   -1){
	        opId = opContent[i].split("#");
		      opName = opName + opId[1] + ',';
		      content = opName.substring(0, (opName.length)-1)
		     }else{
		       opName = opName + opContent[i] + ',';
		       content = opName.substring(0, (opName.length)-2)
		     }
      }
     return content;
		}

