  function	renderOrigval(value, meta, rec, rowIdx, colIdx, ds)
  {
		 var column=rec.get('c_column');
		 var str='';
		 var billsendflag='';
		 if('C_PASSWORD'==column)
		 {
		 		str='*******';
		 }
		else if ('C_BILLSENDFLAG'==column)
		 {	 
		 	 var data=JrafUTIL.paramData("Reconciliation");
						Ext.each(data, function(d)
						{
							if(value === d[0]){
								 str = d[1];
							}
						});
		 }
		 else{
		 		str=value;
		 }	

		 return '<div ext:qtitle="" ext:qtip="' + str + '">'+ str +'</div>';
	};

	 function renderTitle1(value, meta, rec, rowIdx, colIdx, ds)
	 {
		 var vc_exponentcode=rec.get('vc_exponentcode');
		 var str='';
			 if(vc_exponentcode!='')
