			 }
			 else
			 {
			 str=value;
			 }
	    		return '<div ext:qtitle="" ext:qtip="' + str + '">'+ str +'</div>';
	 };

    function formatDate(value){
        return value ? value.dateFormat('Ymd') : new Date().format("Ymd");      
    }

     function formatDate2(value){
     var str = "2099-12-30 00:00:00";
     var date = new Date(str.replace("-","/"));
     return value ? value.dateFormat('Ymd') : date.format("Ymd");      
    }
    function formatMoney(value){
    	var num = Ext.util.Format.number(value,'0,0.00');
    	return '<div ext:qtitle="" ext:qtip="' + num + '">'+ num +'</div>';
    }
