    function dateRender(value){
		   if(value ==null){
		  	 return new Date().format("Ymd");
		   }
		   if(typeof value == "string"){
		   	 return value;
		   }
		 	 return value.format("Ymd");
		 }

	  function dateRender2(value){
		   if(value ==null){
		  	 return '20991231';
		   }
		   if(typeof value == "string"){
		   	 return value;
		   }
		 	 return value.format("Ymd");
		 }
		 var strFormat = function(value){
        		if(value>0){
        			return Math.floor(value/3600)+'小时'+Math.floor((value%3600)/60)+'分钟'+(value%60)+'秒钟';
        		}else{
        			return value;
        		}
        	}


   var c_businflagName=function(v){

    var c_businflag_nm;   
