   function renderStatus(v){
		  var status;
		    if(v=='0'){
		    	status = '未处理';
		    } 
		    if(v=='1'){
		    	status = '确认成功';
		    } 
		    if(v=='2'){
		    	status = '确认失败';
		    } 	
