		    }     
		    return status;
    };

   function call(callno){  
    var l_custnValue = document.getElementById("l_custnValue").value;    		   
			var c_staffno = document.getElementById("c_staffno").value; 	

			var jr=new JrafRequest("dcfund","customer","addOutCallRec"); 
			jr.setExtraPs({l_custno:l_custnValue,c_phone:callno,d_startdate: new Date().format('Ymd'),d_workstarttime:new Date().format('His'),d_workstartdate:new Date().format('Ymd'),c_staffno:c_staffno}); 
			var c_obno;
			jr.setSuccFn(function(a,_resp,xr){
				newRec=a.records[0];            	
				c_obno = newRec.get("c_obno");
				//alert(c_obno);
				window.parent.outCall(l_custnValue,callno,c_obno);
				window.parent.addOutOprLog('11',''); 		
         }); 
			jr.postData();				 
			//var result = window.parent.getMakeCall(callno); 
	}

	function messageSMS(){	  	
	    //window.open('/dcfund/callcenter_management/singlenotetransmit.jsp?mobileno='+callno);
	    var _form=Ext.getCmp('custform');
	    var l_custno =  _form.getForm().findField("l_custno").getValue();
	    var c_callno =  _form.getForm().findField("c_mobileno").getValue();
	    var href = '/dcfund/callcenter_management/singlenotetransmit.jsp?mobileno='+c_callno+'&l_custno='+l_custno ;
	    window.parent.sendMessage(href);	   
	}

