	function setActvFlag(digit,flag){
			var _form=Ext.getCmp('custform');
	    var l_custno=  _form.getForm().findField("l_custno").getValue();
			jr=new JrafRequest('dcfund','customer','updateActiveFlag',{recordType:custinfoRecord,idProperty:'l_custno'});
			jr.setExtraPs({l_custno:l_custno,digit:digit,flag:flag});		
			jr.setSuccFn(function(a,_resp,xr){
				jr2=new JrafRequest('dcfund','customer','getCustomerInfo',{recordType:custinfoRecord,idProperty:'l_custno'});
				jr2.setExtraPs({l_custno:l_custno});
				jr2.setSuccFn(function(a,_resp,xr){
					newRec=a.records[0];            	
					_form.getForm().reset();
	        _form.getForm().loadRecord(newRec);  
		      });	     
				jr2.postData();	
	      });	
			jr.postData();				

	}




	function initActvFlag(){
			var _form=Ext.getCmp('custform');
	    var c_mobileno=  _form.getForm().findField("c_mobileno").getValue();
	    var l_custno=  _form.getForm().findField("l_custno").getValue();
	    var aaa;
	    jr=new JrafRequest('dcfund','customer','getCustomerInfo',{recordType:custinfoRecord,idProperty:'l_custno'});
			jr.setExtraPs({l_custno:l_custno});
			jr.setSuccFn(function(a,_resp,xr){
				newRec=a.records[0];            	
				_form.getForm().reset();
        _form.getForm().loadRecord(newRec);  
        var aaa = newRec.get('c_infovalidflag');
        var c_infovalidflag=  _form.getForm().findField("c_infovalidflag").getValue();
	    	var flag = aaa.substring(4,5);	    		    
	    activeflagwinWin();		  
	    var _formu=Ext.getCmp('mobileactiveflagForm');
	    _formu.getForm().findField("c_mobileno").setValue(c_mobileno);
	    _formu.getForm().findField("flag").setValue(flag);     
	    _formu.getForm().findField("digit").setValue('5');	
	      });	     
			jr.postData();	
	}

		function initPhoneActvFlag(){

			var _form=Ext.getCmp('custform');
	    var c_phone=  _form.getForm().findField("c_phone").getValue();
	    var l_custno=  _form.getForm().findField("l_custno").getValue();
	    var aaa;
	    jr=new JrafRequest('dcfund','customer','getCustomerInfo',{recordType:custinfoRecord,idProperty:'l_custno'});
			jr.setExtraPs({l_custno:l_custno});
			jr.setSuccFn(function(a,_resp,xr){

				newRec=a.records[0];            	
				_form.getForm().reset();
        _form.getForm().loadRecord(newRec);  
        var aaa = newRec.get('c_infovalidflag');
        var c_infovalidflag=  _form.getForm().findField("c_infovalidflag").getValue();
	    	var flag = aaa.substring(2,3);	
	    	//alert(c_infovalidflag);    		    
	    phoneactiveflagwins();		  
	    var _formu=Ext.getCmp('phoneactiveflagForm');
	    _formu.getForm().findField("c_phone").setValue(c_phone);
	    _formu.getForm().findField("phoneflag").setValue(flag);     
	    _formu.getForm().findField("digit").setValue('3');	
	      });	     
			jr.postData();	
	}

