     function getWebMaster(value, p , record){
	    if (Ext.isEmpty(value)){
		    return value;
		}
		else{	
		   return "<a href='/ivr/playRecord.jsp?Uniqueid="+value+"' target='_blank' >录音</a>";

		}
	}

	 function getIVRtoStr(value, p , record)
	 {
	    var strRc= record.get('c_staffno');
	    if (strRc == "2")
		    {
			    return "IVR系统";
			  }
		  else
			  {	
			   return value;
			  }
	}

   function getQuotaName(value, p , record){
		    var _level = record.get('c_sendway');
		    if(_level == 1){return "邮件";}
		    if(_level == 0){return "短信";}
		    if(_level == 2){return "对账单";}

	 	}
	 var ed = new Ext.form.ComboBox({
		            typeAhead: true,
		            triggerAction: 'all',
		            lazyRender: true,
		            mode: 'local',
		            store: new Ext.data.ArrayStore({
		            id: 0,
									fields: ['myId','displayText'],
									data: [['0', '全部'],['1', '持有']]
								}),
									valueField: 'myId',
									displayField: 'displayText'
					  });

				var ed1 = new Ext.form.ComboBox({
		            typeAhead: true,
		            triggerAction: 'all',
		            lazyRender: true,
		            mode: 'local',
		            store: new Ext.data.ArrayStore({
		            id: 0,
