	function getFundTypeName(value, p , record){
	    var _number = record.get('c_columno'); 
	 	  var _no = record.get('c_fundtype');	 	 
	    if(_number == "132" || _number == "131" ){
	      if(_no == 1){
	         return '持有';
	      }else{
		       return '全部';
		    }
		  }
		  else
		     return '';
	}

