 _form = getColForm();

 colwin=Ext.getCmp('colwin');
 if(!colwin){ 
 colwin=new Ext.Window({
		title:'资料修改',
		id:'colwin',
		layout:'fit',
		width:820,
		height:280,
		closeAction:'close',
		plain: true,
		modal: true,
		autoDestroy:true,
		items: _form
		});
		}

		var l_custno = document.getElementById("l_custnValue").value; 		
		modCustInfoStore.setBaseParam('l_custno',l_custno);
	  modCustInfoStore.load({callback:function(recs,opts,success){
	   if(success){
	        var rc=recs[0];
		rc = modCustInfoStore.getAt(0);
	  var _formu2=Ext.getCmp('colForm');	
	  	_formu2.getForm().reset();
	    _formu2.getForm().loadRecord(rc);  
	   }
	}});
	colwin.show();

}  



