 function getTmHisForm(){
   var tmHisForm1 = new Ext.form.FormPanel({
   xxtype : "Jpanel",
  classname : "tmHisForm",
   labelAlign : "right",
  items : [    {
      layout : "column",
      bodyBorder : false,
      items : [        {
          layout : "form",
          items : [            {
              xtype : "datefield",
              fieldLabel : "开始日期",
              name : "c_starttime",
              id : "confirmdate",
              width : 100,
              format : "Ymd"
          }],
          columnWidth : 0.3
      },        {
          layout : "form",
          items : [            {
              xtype : "datefield",
              fieldLabel : "结束日期",
              name : "c_endtime",
              id : "requestdate",
              width : 100,
              format : "Ymd"
          }],
          columnWidth : 0.5
      },        {
          layout : "form",
          items : [            {
              xtype : "button",
              Label : "查询",
              width : 40,
              text : "查 询",
              handler : function(){

												  var startDate = Ext.getCmp('confirmdate').getValue();  
										      var endDate  = Ext.getCmp('requestdate').getValue();									      

												  if(startDate!=""){
													      startDate=Ext.getCmp('confirmdate').getValue().format('Ymd');
													    }

													if(endDate!=""){
													      endDate=Ext.getCmp('requestdate').getValue().format('Ymd');
													    }

													 if((endDate!="")&&(startDate!="")){
																if(endDate<startDate){
															     	   Ext.Msg.show({
																		            title: '消息提示',
																		            msg: '查询结束时间不能小于开始时间!',
																		            minWidth: 300,
																		            modal: true,
																		            icon: Ext.Msg.INFO,				
																		            buttons: Ext.Msg.OK
													  							});
															     	return false;	
																}

														}
	                        tcHisStore.setBaseParam('l_custno',custno);
	                        tcHisStore.setBaseParam('c_starttime',startDate);
	                        tcHisStore.setBaseParam('c_endtime',endDate);
													tcHisStore.setPageInfo(JrafSession.get('PageSize'),'1');
													tcHisStore.load();
	                        }
          }],
          columnWidth : 0.2
      }],
      border : false,
      labelSeparator : ":"
  },    {
      xtype : "grid",
      frame : true,
      viewConfig : {forceFit:true},
      columnLines : true,
      autoHeight : false,
      height : 350,
      store : tcHisStore,
      tbar : [],
      colModel : new Ext.grid.ColumnModel([new Ext.grid.RowNumberer(),
			        {width:40,menuDisabled :true,header:'栏目名称',dataIndex:'c_columname'},
							{width:30,menuDisabled :true,header:'开始时间',dataIndex:'c_starttime'},
							{width:30,menuDisabled :true,header:'结束时间',dataIndex:'c_endtime'},
							{width:30,menuDisabled :true,header:'订阅状态',dataIndex:'c_status'},
							{width:40,menuDisabled :true,header:'操作日期',dataIndex:'c_updatetime'},
							{width:100,menuDisabled :true,header:'备注',dataIndex:'c_memo'}]),loadMask : {msg : '加载数据中，请稍候...'}
  }],
  border : false,
  frame : true,
  autoHeight : false,
  buttonAlign : "center",
  region : "north",
  height : 100
});
  return tmHisForm1;
 }

