function tradeDetail(value, p , record){ 
  			var c_cserialno = record.get('c_cserialno'); 
  			var c_businflag = record.get('c_businflag'); 		
  			var c_assbusinflag = record.get('c_assbusinflag');   				
        return "<a href='#' onclick=viewDocWin('"+c_cserialno+"','"+c_assbusinflag+"') >详细</a>"; 			
    };
function viewDocWin(value,c_assbusinflag){			
			var  formdisplay;
			if('120'==c_assbusinflag||'130'==c_assbusinflag||'122'==c_assbusinflag||'139'==c_assbusinflag||'124'==c_assbusinflag||'142'==c_assbusinflag){
				 formdisplay = getTradeDetailForm1();
			}	else if('138'==c_assbusinflag||'137'==c_assbusinflag){
				 formdisplay = getTradeDetailForm2();
			}	else if('129'==c_assbusinflag){
				 formdisplay = getTradeDetailForm3();
			}	else if('127'==c_assbusinflag){
				 formdisplay = getTradeDetailForm4();
			}	else if('126'==c_assbusinflag||'128'==c_assbusinflag){
				 formdisplay = getTradeDetailForm5();
		  }	else{
		  	formdisplay = getTradeDetailForm6();
		  }		

			var windis=Ext.getCmp("windisplay");
			if(!windis){
				windis=new Ext.Window({
						title:'交易详细',
						id: 'windisplay',
						layout:'fit',
						width:750,
						height:400,
						closeAction:'close',
						plain: true,
						modal: true,						
						autoDestroy:true,
						items: formdisplay,
						buttons : [{
							text : '关闭',
							handler:function(){						
									windis.close();   	
							} 
						}],
						buttonAlign : "center"
				});
			}
		windis.show();
			jr2=new JrafRequest('dcfund','customer','getConfirm',{recordType:Jraf.confirmRecord,idProperty:'c_cserialno'});
			jr2.setExtraPs({c_cserialno:value});

			jr2.setSuccFn(function(a,_resp,xr){	
				newRec=a.records[0];            	
				formdisplay.getForm().reset();
        formdisplay.getForm().loadRecord(newRec);                 
            });
			jr2.postData();	
  };

function accountDetail(value, p , record){ 
  			var c_cserialno = record.get('c_cserialno');  			
        return "<a href='#' onclick=viewAccountWin('"+c_cserialno+"') >详细</a>"; 			
    };
function viewAccountWin(value){    
      accountDetailForm =getAccountDetailForm();
			accountDetailWin=Ext.getCmp('accountDetailWin');
			if(!accountDetailWin){
				accountDetailWin=new Ext.Window({
						title:'账户确认',
						id:'accountDetailWin',
						layout:'fit',
						width:700,
						height:320,
						closeAction:'close',
						plain: true,
						modal: true,
						autoDestroy:true,
						items: accountDetailForm
				});
			}


			var apply = Ext.getCmp("accorequestGrid");
			var applyrec = apply.getSelectionModel().getSelected();		
			jr.setSuccFn(function(a,_resp,xr){
			 var _formu2=Ext.getCmp('accountDetailForm');
			 	_formu2.getForm().reset();
	      _formu2.getForm().loadRecord(applyrec);                      
       });
			jr.postData();	

		accountDetailWin.show();
  };


