							baseParams:	{c_callflag: '0',c_deletemark: '0',c_ibno:temp.c_ibno}
						}),
						listeners: {
							checkchange: function (node, flag) {
								loadRec1(node, flag);
							}
						}
					}]
				},   {
      name : "c_summary",
      fieldLabel : "通话摘要",
      allowBlank : true,
      xtype : "textarea",
      height : 100,
      width : 400
  }],
  buttons : [{
    text : '保存',
    handler : function() {  
     var _form = Ext.getCmp('inBoundsForm');
          var intree = Ext.getCmp('outBoundServerType_tree1');
		 var rs = intree.getChecked('id');
    if (_form.getForm().isValid()){
       _form.getForm().updateRecord(_form.record);
            var rec=_form.record;
            			            if(rs.length >	0) 
														 {
														 var jr1 = new JrafRequest("dcfund", "dcfund_callmanage","updateINServiceType" ,	{recordType: outboudServerRecord});
																 jr1.setExtraPs({c_ibno: rec.get('c_ibno'),c_servtypeno: rs});
																 jr1.postData();
														 }




            var jr=new JrafRequest("dcfund","dcfund_custmanagement","updateInbound",{recordType:inBoundRecord,idProperty:'c_ibno'}); 
					      jr.setExtraPs({c_ibno:rec.get('c_ibno'),c_staffno:rec.get('c_staffno'),c_avayaibno:rec.get('c_avayaibno')}); 
					      jr.setForm(_form);	
