								   inBoundStore.load();
								   inBoundWin.close();
							  });
								jr.postData();

     }
   }
 },{
    text: '关闭',
    handler: function(){
        inBoundWin.close();
    }
 }],
  frame : true,
  buttonAlign : "center",
  allowBlank : true,
  id : "inBoundsForm"
});
  return inBoundsForm1;
