  },  {
   		fieldLabel : "服务类型",
					title: '服务类型',
					xtype: 'panel',
					items: [{
						xtype: "treepanel",
						id:	"outBoundServerType_tree2",
						width: 400,
						height:	220,
						useArrows: true,
						autoScroll:	true,
						animate: true,
						enableDD: true,
						containerScroll: true,
						rootVisible: false,
						frame: true,
						root: new Ext.tree.AsyncTreeNode({id: '0',text:	'呼出服务类型',draggable: false}),
						loader:	new	JrafXmlTreeLoader({
							nparams: {nid: 'c_servtypeno',pid: 'c_parentno',ntext: '{c_servtypename}',loadAll: false,isLeaf: 'childnum',expanded:true,chk:true,chkfld:'chkfld',chkval:'selected'},
							action:	{sysName: "dcfund",oprID: "dcfund_callmanage",actions: "getCallInOROutMainTain"},
							baseParams:	{c_callflag: '1',c_deletemark: '0',c_obno:temp.c_obno}
						}),
						listeners: {
							checkchange: function (node, flag) {
								loadRec2(node, flag);
							}
						}
					}]
				},    {
      name : "c_summary",
      fieldLabel : "通话摘要",
      allowBlank : true,
      xtype : "textarea",
      height : 100,
      width : 400
  }],
  buttons : [{
    text : '保存',
    handler : function() {  
     var _form = Ext.getCmp('outBoundsForm');
     var intree = Ext.getCmp('outBoundServerType_tree2');
		 var rs = intree.getChecked('id');
    if (_form.getForm().isValid()){
       _form.getForm().updateRecord(_form.record);
          	  var rec=_form.record;
	            if (rs.length >	0) {
									 var jr1 = new JrafRequest("dcfund", "dcfund_callmanage","updateObServiceType" ,	{recordType: outboudServerRecord});
											 jr1.setExtraPs({c_obno: rec.get('c_obno'),c_servtypeno: rs});
											 jr1.postData();
									}

	      var jr=new JrafRequest("dcfund","dcfund_custmanagement","updateOutbound",{recordType:outBoundRecord,idProperty:'c_obno'}); 
					      jr.setExtraPs({c_obno:rec.get('c_obno'),c_staffno:rec.get('c_staffno'),c_avayaobno:rec.get('c_avayaobno')}); 
					      jr.setForm(_form);	
							  jr.setSuccFn(function(a,_resp,xr){
								   outBoundStore.load();
								   outBoundWin.close();
							  });
								jr.postData();

     }
   }
 },{
    text: '关闭',
    handler: function(){
        outBoundWin.close();
    }
 }],
  frame : true,
  buttonAlign : "center",
  allowBlank : true,
  id : "outBoundsForm"
});
  return outBoundsForm1;
}




