function accountAppDetail(value, p , record){ 
  			var vc_requestno = record.get('vc_fundacco');  			
        return "<a href='#' onclick=viewAccountAppWin('"+vc_requestno+"') >详细</a>"; 			
    };
function viewAccountAppWin(value){    
     	jr=new JrafRequest('trade','trade_management','getVaccorequestInfo',{recordType:Jraf.AccoReqRecord,idProperty:'vc_requestno'});
			jr.setExtraPs({vc_fundacco:value});			
			var _formu=getAccountAppDetailForm();	
	  	var acco = Ext.getCmp("accoReqGrid");
			var accorec = acco.getSelectionModel().getSelected();
			jr.setSuccFn(function(a,_resp,xr){	
				_formu.getForm().reset();
        _formu.getForm().loadRecord(accorec);  
            });
			jr.postData();	
			accountAppDetailWin=Ext.getCmp('accountAppDetailWin');
			if(!accountAppDetailWin){

				accountAppDetailWin=new Ext.Window({
						title:'账户申请',
						id:'accountAppDetailWin',
						layout:'fit',
						width:800,
						height:380,
						closeAction:'close',
						plain: true,
						modal: true,
						resizable :false,
						autoDestroy:true,
						items: _formu,
						  buttons : [{text : '关闭',handler:function(){						
							accountAppDetailWin.close();   	
						     } 
						}],
						  buttonAlign : "center"
				});
			}
		accountAppDetailWin.show();
  };





