function tradeReqDetail(value, p , record){ 
  			var vc_fundacco = record.get('vc_fundacco');  
  			var flag = record.get('c_businflag');		
        return "<a href='#' onclick=viewTradeReqWin('"+vc_fundacco+"','"+flag+"') >详细</a>"; 			
    };

  function viewTradeReqWin(value,c_businflag){  
     var ReqForm;
     if('020'==c_businflag||'022'==c_businflag||'024'==c_businflag||'039'==c_businflag){					  
					 ReqForm =getTradeReqDetailForm();
				} else if('036'==c_businflag){
				  ReqForm =getTradeReqJJZHDetailForm();
			  } else if('029'==c_businflag){
				  ReqForm =getTradeReqFHDetailForm();
			  }	else if('027'==c_businflag){
				  ReqForm =getTradeReqZRDetailForm();
			  }	else if('026'==c_businflag||'028'==c_businflag){
				  ReqForm =getTradeReqZTGDetailForm();
			  }	else if('090'==c_businflag||'988'==c_businflag||'982'==c_businflag||'985'==c_businflag){
				  ReqForm =getTradeReqDQDetailForm();
				} else if('053'==c_businflag){
				  ReqForm =getTradeReqCXDetailForm();
			  }	else{
			   ReqForm =getTradeReqDetailForm();
			}	

		var	tradeReqDetailWin=Ext.getCmp('tradeReqDetailWin');
			if(!tradeReqDetailWin){
				tradeReqDetailWin=new Ext.Window({
						title:'交易申请',
						id:'tradeReqDetailWin',
						layout:'fit',
						width:700,
						height:450,
						closeAction:'close',
						plain: true,
						modal: true,
						resizable :false,
						autoDestroy:true,
						items: ReqForm,
						buttons : [{text : '关闭',handler:function(){						
								tradeReqDetailWin.close();   	
							} 
						}],
						buttonAlign : "center"
				});
			}
		tradeReqDetailWin.show();

			var apply = Ext.getCmp("trasactionApplyGrid");
			var applyrec = apply.getSelectionModel().getSelected();		
			jr.setSuccFn(function(a,_resp,xr){
			ReqForm.getForm().reset();
      ReqForm.getForm().loadRecord(applyrec);                       
       });
			jr.postData();	
  };


