function viewTfixrequestWin(value){ 
     	jr=new JrafRequest('trade','trade_management','getTfixrequestInfo',{recordType:Jraf.TfixrequestRecord,idProperty:'vc_tradeacco'});
			jr.setExtraPs({vc_fundacco:value});
			var tfixrequest = Ext.getCmp("TfixrequestGrid");
			var rec= tfixrequest.getSelectionModel().getSelected();
			var _formu=getTfixrequestDetailForm();				
			 var vc_exponentcode=rec.get('vc_exponentcode');
			 var str='';
			 var businname=rec.get('c_businname');
			 if(vc_exponentcode!='')
			 	{
				 str=businname+'(智能)';
			 	}
			 else
			 	{
				 str=businname;
				}	

			 _formu.getForm().reset();
	     _formu.getForm().loadRecord(rec);   
	     _formu.getForm().findField('c_businname').setValue(str);
			tfixrequestDetailWin=Ext.getCmp('tfixrequestDetailWin');
			if(!tfixrequestDetailWin){
				tfixrequestDetailWin=new Ext.Window({
						title:'定投协议',
						id:'tfixrequestDetailWin',
						layout:'fit',
						width:750,
						height:490,
						closeAction:'close',
						plain: true,
						modal: true,
						resizable :false,
						autoDestroy:true, 
						items: _formu,
					  buttons : [{text : '关闭',handler:function(){						
								tfixrequestDetailWin.close();   	
					     } 
					}],
					  buttonAlign : "center"
				});
			}
		tfixrequestDetailWin.show();
  };

