			_custTransAccoLoadFlag = false;
  };


   function deepWork(value, p , record){ 
  			var _cstatus = record.get('c_status');  
  			if(_cstatus ==1 || _cstatus ==2){			
           return "<a href='#' onclick='viewManager(0)' >取消</a>"; 	
        }
        	 return "<a href='#' onclick='viewManager(1)' >保留</a>"; 		
    };
    function serverStatus(value, p , record){ 
  			var _cstatus = record.get('c_status');  
  			if(_cstatus ==1 || _cstatus ==2){			
           return "<font color='green'>订制</font>"; 	
        }else{
        	 return "<font color='red'>退订</font>"; 
        }		
    };


    function viewManager(value){
      	var _grid = Ext.getCmp("tmResultGrid");
         Ext.Msg.confirm('提示','是否执行此操作！',function (btn){
					if(btn=='yes'){
					var rec = _grid.getSelectionModel().getSelected();
					var columno = rec.get("c_columno");
          var stime = rec.get("c_starttime");
          var etime = rec.get("c_endtime");
          var cstatus = rec.get("c_status");

          if(typeof stime == "string"){
                rec.set('c_starttime',stime);
              }
          else{
            rec.set('c_starttime',stime.format('Ymd'));
          }
          if(typeof etime == "string"){
            rec.set('c_endtime',etime);
          } else{
            rec.set('c_endtime',etime.format('Ymd'));
          }
					var newFlag = Ext.isEmpty(rec);
         if(!newFlag){
             var jr;
				     var l_custno = document.getElementById("l_custnValue").value; 	
				     if(cstatus!='2'){
			         jr=new JrafRequest("dcfund","dcfund_custmanagement","updateCstminfo",
			                 {recordType:tmRecord,idProperty:'c_cstmno'});
			         jr.setExtraPs({c_status:value,c_cstmno:rec.get('c_cstmno')});
			       }else{
			         jr=new JrafRequest("dcfund","dcfund_custmanagement","insertCstminfo",     
											{recordType:tmRecord,idProperty:'c_cstmno'});  
								jr.setExtraPs({l_custno:temp.l_custno,c_columno:rec.get("c_columno"),c_fundtype:rec.get("c_fundtype"),d_createtime:new Date().format('Ymd'),c_starttime:rec.get("c_starttime"),c_endtime: rec.get("c_endtime"),c_status:'1'});														     									                  
			       }
		         jr.setSuccFn(function(result,_resp,reader){
		         tmResultStore.load();
				         if(columno=='10002'){
					         var jr4 = new JrafRequest('dcfund','cstomer','updateCsInfo',{recordType:custinfoRecord,idProperty:'l_custno'});
						  		    jr4.setExtraPs({c_billsendflag:value,l_custno:l_custno});
						  		    jr4.setSuccFn(function(a,_resp,xr){						  		    
								         jr3=new JrafRequest('dcfund','customer','getCustomerInfo',{recordType:custinfoRecord,idProperty:'l_custno'});
									   		 jr3.setExtraPs({l_custno:l_custno});
									   		 var _formu=Ext.getCmp('custform');
									       jr3.setSuccFn(function(a,_resp,xr){    
											    newRec=a.records[0];          	
											    _formu.getForm().reset();
							            _formu.getForm().loadRecord(newRec); 
						            });
						            jr3.postData();
						              });
												jr4.postData();
									}
		            else{
		            jr3=new JrafRequest('dcfund','customer','getCustomerInfo',{recordType:custinfoRecord,idProperty:'l_custno'});
					   		 jr3.setExtraPs({l_custno:l_custno});
					   		 var _formu=Ext.getCmp('custform');
					       jr3.setSuccFn(function(a,_resp,xr){

						    newRec=a.records[0];          	
						    _formu.getForm().reset();
		            _formu.getForm().loadRecord(newRec); 
		            });
		            jr3.postData();
		            }

		         });
		         jr.postData();
			}}
})
    }


var passwordWin;
function openPasswordWin(){
 var rec;
 _form = Ext.getCmp('passwordForm'); 
 passwordWin=Ext.getCmp('passwordWin');
 if(!passwordWin){ 
 passwordWin= new Ext.Window({
                        title:'密码修改', id:'passwordWin', 
                        layout:'fit', width:500,height:200, 
                        closeAction:'hide',plain: true,  
                        modal: true,autoDestroy:false, 
                        items: _form
                        });
}  
 passwordWin.show();
 _form.getForm().reset();
}


