      jr.setExtraPs({l_custno:value});
     jr.setSuccFn(function(a,_resp,xr){

   });
  jr.postData();
  }});
}



function loadRec(node,value){
		deepStore.setBaseParam('c_typeno',node);
    deepStore.load();
	  var voteIframe=document.getElementById("voteIframe");

    voteIframe.src="/dcfund/customer_management/deepshow.jsp?c_typeno="+node+"&l_custno="+temp.l_custno+"&c_deepno="+value;	
};




