   function openStrasactopmfrimWin(){
   var _gird=Ext.getCmp("dateTrasactionGrid");
   var _record=_gird.getSelectionModel().getSelected();

         if( _record.get('proFession')=='申购'){ 
                   window.showModalDialog('applypurchase.jsp',0,"dialogWidth:700px;dialogHeight:250px;status:no;help:no;");  
         }

         else if( _record.get('proFession')=='赎回'){
       window.showModalDialog('redeem_detail.jsp',0,"dialogWidth:760px;dialogHeight:200px;status:no;help:no;"); 
         }

         else if( _record.get('proFession')=='基金转换'){

                window.showModalDialog('fundtransfer.jsp',0,"dialogWidth:700px;dialogHeight:250px;status:no;help:no;"); 
         } 
           else if( _record.get('proFession')=='撤单'){
                  window.showModalDialog('withdraw_order.jsp',0,"dialogWidth:700px;dialogHeight:250px;status:no;help:no;"); 
         }
           else if( _record.get('proFession')=='定投协议'){
                  window.showModalDialog('changetotrusteeship_transferto.jsp',0,"dialogWidth:700px;dialogHeight:250px;status:no;help:no;"); 
         }   
          else{
        window.showModalDialog('strasaction_detail.jsp',0,"dialogWidth:700px;dialogHeight:250px;status:no;help:no;"); 
         }



    }


