    function showMore(){			
        var colwin=Ext.getCmp('showForm');   
        if(colwin.isVisible()){       
        colwin.setVisible(false);
        }
        else{      
        colwin.setVisible(true);
        } 
		Ext.getCmp('MainPanel').doLayout();
    }

   function closeWin(){
      var colwin=Ext.getCmp('showForm');
        colwin.hide();
    }

