Ext.extend(TaInfoForm, Ext.FormPanel,{});	

	function setCommunication (columname,columvalue){
				var _formu=Ext.getCmp('custform');
							var jr=new JrafRequest('dcfund','cstomer','updateCommunication',{recordType:taRecord,idProperty:'l_custno'});
							jr.setExtraPs({l_custno:temp.l_custno,columname:columname,columvalue:columvalue});
							jr.setSuccFn(function(a,_resp,xr){
									modCustInfoStore.setBaseParam('l_custno',temp.l_custno);
								  modCustInfoStore.load({callback:function(recs,opts,success){
								   if(success){
								      var rc=recs[0];
									  	rc = modCustInfoStore.getAt(0);
									  	_formu.getForm().reset();
									    _formu.getForm().loadRecord(rc);  
								   }}});
							});
							jr.postData();
		}

Scripts.onLoad:


       temp.l_custno = document.getElementById("l_custnValue").value;

			  custno = document.getElementById("l_custnValue").value;

				var l_custno = document.getElementById("l_custnValue").value; 	
				shareStore.setBaseParam('l_custno',l_custno);
				shareStore.load({
      	  callback: function(){
      	  	var sharecount = shareStore.getTotalCount();
						var sharesum=0;
						var profitsum = 0;
						var vssum = 0;
						for(var i=0;i<sharecount;i++){
						  var sharerec = shareStore.getAt(i);
						  sharesum +=Number(sharerec.get('f_realshares'));
						  profitsum +=Number(sharerec.get('floatprofit'));
						  vssum +=Number(sharerec.get('vs'));
						}

			     var _sharegrid =  Ext.getCmp('shareGrid');
			     _sharegrid.setTitle("份额合计："+Ext.util.Format.number(sharesum,'0,0.00')+"　　盈亏合计："+Ext.util.Format.number(profitsum,'0,0.00')+"　　市值合计："+Ext.util.Format.number(vssum,'0,0.00'));
      	  }

				});
				confirmStore.setBaseParam('l_custno',l_custno);
				confirmStore.load();

			jr=new JrafRequest('dcfund','customer','getCustomerInfo',{recordType:custinfoRecord,idProperty:'l_custno'});
			jr.setExtraPs({l_custno:l_custno});
			var _formu=Ext.getCmp('custform');
			var _formb=Ext.getCmp('showForm');
			jr.setSuccFn(function(a,_resp,xr){	

				newRec=a.records[0];    

				_formu.getForm().reset();
        _formu.getForm().loadRecord(newRec);  
        _formb.getForm().reset();
        _formb.getForm().loadRecord(newRec);


    var _form    = Ext.getCmp('custform');
    var c_mobileno = 	_form.getForm().findField("c_mobileno").getValue();    
    var c_phone = 	_form.getForm().findField("c_phone").getValue();
    window.parent.getCallNo(l_custno,c_mobileno,c_phone);
	     });
			jr.postData();

