    var c_type = v.split(',')[0];
    var c_itype = v.split(',')[1];     	
		    var c_identitytype;
		    if('0'==c_type){
		    if(c_itype=='0'){
		    	c_identitytype = '身份证';
		    } 
		     if(c_itype=='1'){
		    	c_identitytype = '营业执照';
		    } 	
