		    } 
		     if(c_itype=='8'){
		    	c_identitytype = '其他机构';
		    } 
		  }
		  if('1'==c_type){
		    if(c_itype=='0'){
		    	c_identitytype = '身份证';
		    } 
		     if(c_itype=='1'){
		    	c_identitytype = '中国护照';
