       centernoText = '民生网银分中心';
    }
    if(v=='G000'){
       centernoText = '招行网银分中心';
    }  
    if(v=='5000'){
       centernoText = '好易联分中心';
    }
    if(v=='4000'){
       centernoText = '工行网银分中心';
    }
