		     if(c_itype=='3'){
		    	c_identitytype = '士兵证';
		    }
		     if(c_itype=='4'){
		    	c_identitytype = '回乡证';
		    }
		     if(c_itype=='5'){
		    	c_identitytype = '户口本';
		    }
		     if(c_itype=='6'){
		    	c_identitytype = '外籍护照';
		    }
		     if(c_itype=='7'){
		    	c_identitytype = '其他';
