{
  xtype : "form",
  frame : true,
  classname : "passwordForm",
  id : "passwordForm",
  xxtype : "Jpanel",
  items : [    {
      layout : "column",
      items : [        {
          layout : "form",
          items : [            {
              xtype : "textfield",
              fieldLabel : "新密码",
              inputType : "password",
              allowBlank : false,
              maxLength : 8,
              name : "newpwd",
              width : 200
          },            {
              xtype : "textfield",
              inputType : "password",
              fieldLabel : "确认密码",
              allowBlank : false,
              name : "newpwd2",
              vtypeText : "两次密码不一致",
              width : 200
          }]
      }],
      labelSeparator : ":",
      border : false
  }],
  buttons : [{
			  text:'保存', handler:function(){
			  var _passwordform=Ext.getCmp('passwordForm');

			  var _newpwd = _passwordform.getForm().findField("newpwd").getValue();
			  var _newpwd2 = _passwordform.getForm().findField("newpwd2").getValue();

			  if(_newpwd == _newpwd2.trim()){	
			      Ext.Msg.confirm('保存数据','确认保存已修改数据?',function(btn){if(btn == 'yes'){					   
						var jr=new JrafRequest("dcfund","cstomer","updatePassword");

								  jr.setExtraPs({l_custno:custno,c_password:_newpwd});
									jr.setForm(_passwordform);
                jr.setSuccFn(function(result,_resp,reader){
                     passwordWin.hide();
                });
                 jr.postData();
			  }});
					}else{
					       Ext.MessageBox.alert("密码提示","两次密码不一致！！");
					}
} } ],
  buttonAlign : "center",
  border : false
}

